import './App.css';
import 'bootstrap/dist/css/bootstrap.css';
import React, {useEffect, useState} from 'react';
import {ProductList} from "./components/ProductList";
import {Summary} from "./components/Summary";
import axios from "axios";

function App() {
    const [products, setProducts] = useState([
        {id: 'uuid-1', productName: '콜롬비아커피 1', category: '커피빈', price: 5000},
        {id: 'uuid-2', productName: '콜롬비아커피 2', category: '커피빈', price: 5000},
        {id: 'uuid-3', productName: '콜롬비아커피 3', category: '커피빈', price: 5000}
    ]);

    const [items, setItems] = useState([]);

    const handleAddClicked = id => {
        const product = products.find(v => v.id === id);
        const found = items.find(v => v.id === id);
        const updateItems =
            found ? items.map(v => (v.id === id) ? {...v, count: v.count + 1} : v) : [...items, {...product, count: 1}];
        setItems(updateItems);
    }
    useEffect(() => {

    }, [])
    return (
        <div className="container-fluid">
            <div className="row justify-content-center m-4">
                <h1 className="text-center">Grids & Circle</h1>
            </div>
            <div className="card">
                <div className="row">
                    <div className="col-md-8 mt-4 d-flex flex-column align-items-start p-3 pt-0">
                        <ProductList products={products} onAddClick={handleAddClicked}/>
                    </div>
                    <Summary items={items}/>
                </div>
            </div>
        </div>
    );
}

export default App;
