import {SummaryItem} from "./SummaryItem";
import React, {useState} from "react";

export function Summary({items = []}) {
    const totalPrice = items.reduce((prev, curr) => prev + (curr.price * curr.count), 0)
    const [order, setOrder] = useState({});
    const changeEmailInput = (e) => {
        setOrder({...order, email: e.tartget.value});
    }
    const changeAddressInput = (e) => {
        setOrder({...order, address: e.tartget.value});
    }
    return (
        <>
            <div className="col-md-4 summary p-4">
                <div>
                    <h5 className="m-0 p-0"><b>Summary</b></h5>
                </div>
                <hr/>
                {items.map(v => <SummaryItem key={v.productId} count={v.count} productName={v.productName}/>)}
                <form>
                    <div className="mb-3">
                        <label htmlFor="email" className="form-label">이메일</label>
                        <input type="email" className="form-control mb-1" value={order.email} onChange={changeEmailInput} id="email"/>
                    </div>
                    <div className="mb-3">
                        <label htmlFor="address" className="form-label">주소</label>
                        <input type="text" className="form-control mb-1" value={order.address} onChange={changeInput} id="address"/>
                    </div>
                    <div className="mb-3">
                        <label htmlFor="postcode" className="form-label">우편번호</label>
                        <input type="text" className="form-control" value={order.postcode} onChange={changeInput} id="postcode"/>
                    </div>
                    <div>당일 오후 2시 이후의 주문은 다음날 배송을 시작합니다.</div>
                </form>
                <div className="row pt-2 pb-2 border-top">
                    <h5 className="col">총금액</h5>
                    <h5 className="col text-end">{totalPrice}원</h5>
                </div>
                <button className="btn btn-dark col-12">결제하기</button>
            </div>
        </>
    )
}
